        props.fun("alert alert-success alert-dismissible w-25 mx-auto d-none fade")
    }

    return (
        <div class={alertDiv} role="alert">
            {props.txt}
            <button type="button" class="btn-close btn-close" aria-label="Close" onClick={hideAlert}></button>
        </div>
    )
}
